mapper.select:
    	 SELECT alias.KAPTCODE
  			FROM (
  			  select KAPTCODE from apartment
  				where dongname = #{dongname}
  				and kaptname like #{aptname}
  				ORDER BY KAPTDACNT DESC
 			)alias
		 WHERE ROWNUM = 1

select count(*) from apartment
group by dongname
having dongname = #{dongname}
